    private static HashMap<String, State> map = new HashMap<>();
    private List<String> empty = new ArrayList<>();

    static {
        init();
    }

    private static void init() {
        for (int i = 0; i < states.length; i++) {
            List<String> list = new ArrayList<>();
            for (String city: cities[i]) {
                list.add(city);
            }
            map.put(states[i], new State(statesShort[i], list));
        }
    }

    public List<String> getCities(String state) {
        List<String> value = map.get(state.trim().toLowerCase()).getCities();
        return value != null ? value : empty;
    }

    public String getShortState(String state) {
        if (state == null)
            return "";

        State st = map.get(state.trim().toLowerCase());

        return st != null ? st.getState() : null;
    }

    private static class State {
        private String state;
        private List<String> cities;

        public State(String state, List<String> cities) {
            this.state = state;
            this.cities = cities;
        }

        public String getState() {
            return state;
        }

        public void setState(String state) {
            this.state = state;
        }

        public List<String> getCities() {
            return cities;
        }

        public void setCities(List<String> cities) {
            this.cities = cities;
        }
    }
